mapper.insert:
/* IMMmbrEduMapper.insert */ INSERT INTO im_mmbr_edu ( mmbr_edu_id, member_srl, scl_nm, mjr_nm, spec_nm, dge_cdv, dge_ymd, dge_file_id, rech_tpc,
del_yn, frst_reger_id, frst_reg_dt, frst_reger_ip ) VALUES ( #{mmbrEduId, jdbcType=VARCHAR}, #{memberSrl, jdbcType=VARCHAR}, #{sclNm, jdbcType=VARCHAR}, #{mjrNm, jdbcType=VARCHAR}, #{specNm, jdbcType=VARCHAR}, #{dgeCdv, jdbcType=VARCHAR}, #{dgeYmd, jdbcType=VARCHAR}, #{dgeFileId, jdbcType=VARCHAR}, #{rechTpc, jdbcType=VARCHAR},
'N', #{frstRegerId, jdbcType=VARCHAR}, TO_CHAR(SYSDATETIME, 'yyyymmddhh24miss'), #{frstRegerIp, jdbcType=VARCHAR} )

mapper.select:
/* IMMmbrEduMapper.selectDetail */ SELECT A.mmbr_edu_id AS "mmbrEduId", A.member_srl AS "memberSrl", A.scl_nm AS "sclNm", A.mjr_nm AS "mjrNm", A.spec_nm AS "specNm", A.dge_cdv AS "dgeCdv", A.dge_ymd AS "dgeYmd", A.dge_file_id AS "dgeFileId", A.rech_tpc AS "rechTpc", A.del_yn AS "delYn", A.frst_reger_id AS "frstRegerId", A.frst_reg_dt AS "frstRegDt", A.frst_reger_ip AS "frstRegerIp", A.last_mdfer_id AS "lastMdferId", A.last_mdfcn_dt AS "lastMdfcnDt", A.last_mdfer_ip AS "lastMdferIp"
FROM im_mmbr_edu A WHERE mmbr_edu_id = #{mmbrEduId}
AND A.del_yn = 'N'
