mapper.insert:
/* IMMmbrEtcMapper.insert */ INSERT INTO im_mmbr_etc ( mmbr_etc_id, member_srl, etc_itm, etc_evddoc_id,
del_yn, frst_reger_id, frst_reg_dt, frst_reger_ip ) VALUES ( #{mmbrEtcId, jdbcType=VARCHAR}, #{memberSrl, jdbcType=VARCHAR}, #{etcItm, jdbcType=VARCHAR}, #{etcEvddocId, jdbcType=VARCHAR},
'N', #{frstRegerId, jdbcType=VARCHAR}, TO_CHAR(SYSDATETIME, 'yyyymmddhh24miss'), #{frstRegerIp, jdbcType=VARCHAR} )

mapper.update:
/* IMMmbrEtcMapper.update */ UPDATE im_mmbr_etc SET etc_itm = #{etcItm, jdbcType=VARCHAR}, etc_evddoc_id = #{etcEvddocId, jdbcType=VARCHAR},
last_mdfer_id = #{lastMdferId, jdbcType=VARCHAR}, last_mdfcn_dt = TO_CHAR(SYSDATETIME, 'yyyymmddhh24miss'), last_mdfer_ip = #{lastMdferIp, jdbcType=VARCHAR} WHERE mmbr_etc_id = #{mmbrEtcId}
AND del_yn = 'N'

mapper.select:
/* IMMmbrEtcMapper.selectDetail */ SELECT A.mmbr_etc_id AS "mmbrEtcId", A.member_srl AS "memberSrl", A.etc_itm AS "etcItm", A.etc_evddoc_id AS "etcEvddocId", A.del_yn AS "delYn", A.frst_reger_id AS "frstRegerId", A.frst_reg_dt AS "frstRegDt", A.frst_reger_ip AS "frstRegerIp", A.last_mdfer_id AS "lastMdferId", A.last_mdfcn_dt AS "lastMdfcnDt", A.last_mdfer_ip AS "lastMdferIp"
FROM im_mmbr_etc A WHERE mmbr_etc_id = #{mmbrEtcId}
AND A.del_yn = 'N'

/* IMMmbrEtcMapper.selectList */
SELECT A.mmbr_etc_id AS "mmbrEtcId", A.member_srl AS "memberSrl", A.etc_itm AS "etcItm", A.etc_evddoc_id AS "etcEvddocId", A.del_yn AS "delYn", A.frst_reger_id AS "frstRegerId", A.frst_reg_dt AS "frstRegDt", A.frst_reger_ip AS "frstRegerIp", A.last_mdfer_id AS "lastMdferId", A.last_mdfcn_dt AS "lastMdfcnDt", A.last_mdfer_ip AS "lastMdferIp"
FROM im_mmbr_etc A WHERE A.del_yn = 'N'
AND member_srl = #{memberSrl, jdbcType=VARCHAR}
ORDER BY A.frst_reg_dt DESC
